import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import pic1 from "./pics/fireplanet1.png";
import pic2 from "./pics/fireplanet2.PNG";
import $ from "jquery";
import ArtBody from "./components/ArtBody";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { rows: [], query: "" };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    this.performSearch("Paintings");
  }

  handleChange(event) {
    this.setState({ query: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
  }

  performSearch(classificationInput) {
    const getString =
      "https://api.harvardartmuseums.org/object?apikey=19142ab0-6cf9-11ea-8bdc-95909fa24347&classification=" +
      classificationInput +
      "&q=colorcount:>=5";
    // https://api.harvardartmuseums.org/object?person=33430?apikey=19142ab0-6cf9-11ea-8bdc-95909fa24347

    $.ajax({
      url: getString,
      success: searchResults => {
        console.log("data retrieved");
        console.log(searchResults);
        const results = searchResults.records;
        var artworkCards = [];

        results.forEach(artwork => {
          console.log(artwork);
          const art = <ArtBody piece={artwork}></ArtBody>;
          artworkCards.push(art);
        });
        this.setState({ rows: artworkCards });
      },
      error: (xhr, status, err) => {
        console.log("error");
      }
    });
  }

  render() {
    return (
      <div className="App">
        <header className="toolbar">
          <ul>
            <li className="title">Cooper Hewitt</li>
            <li className="about">About</li>
          </ul>
        </header>
        <form onSubmit={this.handleSubmit}>
          <label>
            Pick your favorite flavor:
            <select value={this.state.query} onChange={this.handleChange}>
              <option value="Paintings">Paintings</option>
              <option value="Mosaics">Mosaics</option>
              <option value="Drawings">Drawings</option>
              <option value="Textile%20Arts">Textiles</option>
              <option value="Stained%20Glass">Stained Glass</option>
              <option value="Photographs">Photographs</option>
              <option value="Prints">Prints</option>
            </select>
          </label>
          <input type="submit" value="Submit" />
        </form>
        <div class="contentContainer">{this.state.rows}</div>
      </div>
    );
  }
}

export default App;
