    //     <div className="card">
    //       <h2>title:</h2>
    //       <p>{artwork.title}</p>{" "}
    //     </div>
    //   );
    // });
    this.performSearch();
  }

  performSearch(classificationInput) {
    // var pieces;
    // var apiEndpointBaseURL = "https://api.harvardartmuseums.org/object";

    // var queryString = $.param({
    //   apikey: "19142ab0-6cf9-11ea-8bdc-95909fa24347",
    //   title: "rabbit",
    //   classification: "Paintings"
    // });

    // $.getJSON(apiEndpointBaseURL + "?" + queryString, function(data) {
    //   console.log(data.records);
    //   pieces = data.records;
    //   var artworkCards = [];
    //   pieces.forEach(piece => {
    //     var artworkCard = <ArtBody piece={piece}></ArtBody>;
    //     console.log(artworkCard);
    //     artworkCards.push(artworkCard);
    //   });
    //   console.log(artworkCards);
    //   this.setState = { rows: artworkCards };
    // });

    const getString =
      "https://api.harvardartmuseums.org/object?apikey=19142ab0-6cf9-11ea-8bdc-95909fa24347&classification=Paintings&q=colorcount:>=7";
    // https://api.harvardartmuseums.org/object?person=33430?apikey=19142ab0-6cf9-11ea-8bdc-95909fa24347

    $.ajax({
      url: getString,
      success: searchResults => {
        console.log("data retrieved");
        console.log(searchResults);
        const results = searchResults.records;
        var artworkCards = [];

        results.forEach(artwork => {
          console.log(artwork);
          const art = <ArtBody piece={artwork}></ArtBody>;
          artworkCards.push(art);
        });
        this.setState({ rows: artworkCards });
      },
      error: (xhr, status, err) => {
        console.log("error");
      }
    });
  }

  render() {
    return (
      <div className="App">
        <header className="toolbar">
          <ul>
            <li className="title">Cooper Hewitt</li>
            <li className="about">About</li>
          </ul>
        </header>
        <div class="contentContainer">{this.state.rows}</div>
      </div>
    );
  }
}

export default App;
